script:
frappe.ui.form.on("Employee", {
  refresh(frm) {
    // Run AFTER other scripts / ajax changes
    frappe.after_ajax(() => {
      setTimeout(() => {
        const f = frm.fields_dict.naming_series;
        if (!f) return;

        // Make absolutely sure it's not required or visible
        frm.set_df_property("naming_series", "reqd", 0);
        frm.set_df_property("naming_series", "hidden", 1);
        frm.toggle_display("naming_series", false);

        // Extra paranoia: nuke wrapper display
        if (f.wrapper) {
          f.wrapper.style.display = "none";
        }

        console.log("Final hide naming_series applied for:", frm.doc.name);
      }, 300);  // small delay so this wins after other refresh handlers
    });
  },
});
